import {React,useState, useEffect } from 'react';
import './categoryList.css'; // Create a CSS file for styling
import { FaEdit, FaTrash, FaEye } from 'react-icons/fa'; // Example icons
import axios from 'axios';

function CategoryList() {
    const [categories, setCategories] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchCategories = async () => {
            try {
                const response = await axios.get(
                    "http://ec2-13-232-64-63.ap-south-1.compute.amazonaws.com:3000/api/v1/admin/category/list?page=1&perPage=10&nameMatched=&isActive=all",
                    {
                        headers: {
                            'Authorization': `Bearer ${localStorage.getItem('token')}`, // Example header
                            'Content-Type': 'application/json',
                            // Add other headers as needed
                        }
                    }
                );
                setCategories(response.data); // Assuming the API returns an array of categories
                setLoading(false);
            } catch (err) {
                setError(err.message || "Failed to fetch categories");
                setLoading(false);
            }
        };

        fetchCategories();
    }, []); // Empty dependency array means this effect runs once on mount

    if (loading) return <p>Loading...</p>;
    if (error) return <p style={{ color: 'red' }}>{error}</p>;

    return (
        <div className="category-list-container">
            <h2>Category List</h2>
            <table className="category-table">
                <thead>
                    <tr>
                        <th>Category Name</th>
                        <th>Arabic Category Name</th>
                        <th>No. of Sub Categories</th>
                        <th>No. of Sub Sub-Categories</th>
                        <th>Status</th>
                        <th>Last Updated On</th>
                        <th>Specification</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {categories.map(category => (
                        <tr key={category.id}>
                            <td>{category.name}</td>
                            <td>{category.arabicName}</td>
                            <td>{category.subCategories}</td>
                            <td>{category.subSubCategories}</td>
                            <td>{category.status}</td>
                            <td>{category.lastUpdated}</td>
                            <td>{category.specification}</td>
                            <td>
                                <a href={`/edit/${category.id}`}><FaEdit /></a>
                                <a href={`/view/${category.id}`}><FaEye /></a>
                                <a href={`/delete/${category.id}`}><FaTrash /></a>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default CategoryList;
