import React, { useState, useEffect } from 'react';
import './userList.css'; // Create a CSS file for styling
import { FaEye, FaTrash, FaSync } from 'react-icons/fa'; // Example icons
import axios from 'axios';

function UserList() {
    const [users, setUsers] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const response = await axios.get("http://ec2-13-232-64-63.ap-south-1.compute.amazonaws.com:3000/api/v1/admin/user/list?role=user", {
                    headers: {
                        'Authorization': localStorage.getItem('token'),
                        'Content-Type': 'application/json',
                    }
                });
                console.log(response.data,"kddkdkd")
                setUsers(response.data.data.findUserDetails); // Assuming the API returns an array of users
                setLoading(false);
            } catch (err) {
                setError(err.message || "Failed to fetch users");
                setLoading(false);
            }
        };

        fetchUsers();
    }, [currentPage]);

    if (loading) return <p>Loading...</p>;
    if (error) return <p style={{ color: 'red' }}>{error}</p>;

    return (
        <div className="user-list-container">
            <h2>User List</h2>
            <table className="user-table">
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email ID</th>
                        <th>Phone Number</th>
                        <th>Address</th>
                        <th>Created At</th>
                        <th>Status</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {/* {users.map(user => (
                        <tr key={user._id}>
                            <td>{user.name}</td>
                            <td>{user.email}</td>
                            <td>{user.phoneNumber}</td>
                            <td>{user.address}</td>
                            <td>{new Date(user.createdAt).toLocaleDateString()}</td>
                            <td>{user.status}</td>
                            <td>
                                <a href={`/users/view/${user.id}`}><FaEye /></a>
                                <a href={`/users/delete/${user.id}`}><FaTrash /></a>
                                <a href={`/users/update-status/${user.id}`}><FaSync /></a>
                            </td>
                        </tr>
                    ))} */}
                    {
                        users.map(user=>(
                            <tr key = {user._id}>
                                <td>{user.name}</td>
                                <td>{user.email}</td>
                                <td>{user.phoneNumber}</td>
                                <td>{user.address}</td>
                                <td>{user.createdAt}</td>
                                <td>{user.isActive?'Active':"Inactive"}</td>
                                <td>
                                    <a href={`/users/view/${user._id}`}><FaEye/></a>
                                    <a href={`/users/delete/${user._id}`}><FaTrash/></a>
                                    <a href={`/users/update-status/${user._id}`}><FaSync/></a>

                                </td>
                            </tr>
                        ))
                    }
                </tbody>
            </table>
            <div className="pagination" id='pagi'>
                <button>
                    Previous
                </button>
                <span>Page {currentPage} of {totalPages}</span>
                <button>
                    Next
                </button>
            </div>
        </div>

    );
}

export default UserList;
