import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

function EditCategory() {
    const { id } = useParams(); // Get category ID from URL
    const navigate = useNavigate();
    const [categoryName, setCategoryName] = useState('');
    const [arabicCategoryName, setArabicCategoryName] = useState('');
    const [status, setStatus] = useState('Active');
    const [imageUrl, setImageUrl] = useState(''); // State for image URL
    const [error, setError] = useState('');

    useEffect(() => {
        // Fetch category details to pre-fill the form
        const fetchCategory = async () => {
            try {
                const response = await axios.get(`http://ec2-13-232-64-63.ap-south-1.compute.amazonaws.com:3000/api/v1/admin/category/${id}`, {
                    headers: {
                        'Authorization': localStorage.getItem('token'),
                        'Content-Type': 'application/json',
                    }
                });
                const category = response.data.data;
                setCategoryName(category.name);
                setArabicCategoryName(category.ar_name);
                setStatus(category.isActive ? 'Active' : 'Inactive');
                setImageUrl(category.imageUrl); // Assuming the API returns an image URL
            } catch (err) {
                setError('Failed to fetch category details');
            }
        };

        fetchCategory();
    }, [id]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.put(`http://ec2-13-232-64-63.ap-south-1.compute.amazonaws.com:3000/api/v1/admin/category/${id}`, {
                name: categoryName,
                ar_name: arabicCategoryName,
                isActive: status === 'Active',
            }, {
                headers: {
                    'Authorization': localStorage.getItem('token'),
                    'Content-Type': 'application/json',
                }
            });
            navigate('/categories'); // Redirect to category list after successful update
        } catch (err) {
            setError('Failed to update category');
        }
    };

    return (
        <div className="edit-category-container">
            <h2>Edit Category</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="categoryName">Category Name:</label>
                    <input
                        type="text"
                        id="categoryName"
                        value={categoryName}
                        onChange={(e) => setCategoryName(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="arabicCategoryName">Arabic Category Name:</label>
                    <input
                        type="text"
                        id="arabicCategoryName"
                        value={arabicCategoryName}
                        onChange={(e) => setArabicCategoryName(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="status">Status:</label>
                    <select
                        id="status"
                        value={status}
                        onChange={(e) => setStatus(e.target.value)}
                    >
                        <option value="Active">Active</option>
                        <option value="Inactive">Inactive</option>
                    </select>
                </div>
                {imageUrl && (
                    <div className="form-group">
                        <label>Category Image:</label>
                        <img src={imageUrl} alt="Category" style={{ maxWidth: '100%', height: 'auto' }} />
                    </div>
                )}
                <button type="submit">Save Changes</button>
            </form>
        </div>
    );
}

export default EditCategory;
