import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import { clear_localStorage } from './helper';

function ViewUser() {
    const { id } = useParams();
    const navigate = useNavigate();
    // const [status, setStatus] = useState('Active');
    const [error, setError] = useState('');


    return (
        <div className="edit-category-container">
            <h2>View User</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <form>
                <div className="form-group">
                    <label htmlFor="categoryName">User Name</label>
                    <input
                        type="text"
                        id="userName"
                        // value={}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="categoryName">Email</label>
                    <input
                        type="text"
                        id="email"
                        // value={}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="categoryName">Phone Number</label>
                    <input
                        type="text"
                        id="userName"
                        // value={}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="categoryName">Address</label>
                    <input
                        type="text"
                        id="userName"
                        // value={}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="status">Status:</label>
                    <select
                        id="status"
                    // value={ }
                    >
                        <option value="Active">Active</option>
                        <option value="Inactive">Inactive</option>
                    </select>
                </div>
            </form>
        </div>
    );
}

export default ViewUser;
